import { useParams } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { getDogDetails, cleanDogDetails } from "../../Redux/actions" 
import React from "react";
import styles from "./BreedDetail.modules.css";
import LoadingScreen from "../LoadingScreen/LoadingScreen"

const BreedDetail = () => {

    const { name } = useParams();
    const dispatch = useDispatch();
    const breed = useSelector((state) => state.dogDetails)

    useEffect(() => {
        dispatch(getDogDetails(name));
        return () => dispatch(cleanDogDetails());
    }, []);

    if (!breed) {
        return (
            <LoadingScreen />
        )
    }

    return (
        <>
            <div className = {styles.body}>
                <span>{breed.name}</span>
                <span>{breed.height}</span>
                <img src = {breed.img}/>
            </div>
        </>
    )
}

export default BreedDetail;
