import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useEffect } from "react";
import { getDBBreeds, deleteBreed } from "../../Redux/actions";
import TitleBar from "../TitleBar/TitleBar";

export default function DeleteBreed() {
  const dispatch = useDispatch();

  const breeds = useSelector((state) => state.dbBreeds);

  const [selectedBreed, setSelectedBreed] = useState(null);

  const handleBreedChange = (event) => {
    const selectedId = event.target.value;
    setSelectedBreed(breeds.find((breed) => breed.id === selectedId));
  };

  const handleDeleteBreed = () => {
    if (selectedBreed) {
      dispatch(deleteBreed(selectedBreed.id));
      setSelectedBreed(null);
    }
  };

  useEffect(() => {
    dispatch(getDBBreeds());
  }, []);

  return (
    <>
        <TitleBar type={"detail"} text={"Delete a breed from the DB"}/>
        
        <div className="deleteBody">
            <select value={selectedBreed ? selectedBreed.id : ""} onChange={handleBreedChange} className="deleteSelector">
                <option value="">Selecciona una raza</option>
                {breeds.map((breed) => (
                    <option key={breed.id} value={breed.id}>
                        {breed.name}
                    </option>
                ))}
            </select>

            <h2>Detalles del perro seleccionado:</h2>
            {selectedBreed && (
                <div>
                    <p>Nombre: {selectedBreed.name}</p>
                    <p>Edad: {selectedBreed.lifeSpan || selectedBreed.life_span}</p>
                    <button onClick={handleDeleteBreed}>Eliminar perro</button>
                </div>
            )}
        </div>
    </>
  );
}
