import { GET_ALL_DOGS, GET_DOG_DETAILS, CLEAN_DOG_DETAILS } from "./action-types";

export const getAllDogs = async() => {
    return new Promise((resolve) => {
      const unsubscribe = store.subscribe(() => {
        const state = store.getState();
        if (state.dogs.length > 0) {
          unsubscribe();
          resolve(state.dogs);
        }
      });
  
      store.dispatch(fetchDogs());
    });
  }
  
export const getDogDetails = (name) => {
    return function(dispatch) {
        fetch(`http://localhost:3001/dogs?name=${name}`)
        .then(result => result.json())
        .then(dog => {
            console.log(dog)
            return dispatch({type: GET_DOG_DETAILS, payload: dog})
        })
    }
}

export const cleanDogDetails = () => {
    return {type: CLEAN_DOG_DETAILS}
}
