import { useSelector, useDispatch } from "react-redux";
import { useEffect, useState } from "react";
import BreedCard from "../BreedCard/BreedCard";
import LoadingScreen from "../LoadingScreen/LoadingScreen"
import { getAllDogs } from "../../Redux/actions";
import NavBar from "../NavBar/NavBar";
import "./Home.modules.css"
/*--------------------------------------------------------------------*/

const Home = () => {

    const dispatch = useDispatch();

    const [sortByNameAsc, setSortByNameAsc] = useState(true);
    const [sortByWeightAsc, setSortByWeightAsc] = useState(true);
    
    const breeds = useSelector((state) => state.dogs);

    console.log(breeds.length)
    console.log(breeds)

    useEffect(() => {
        dispatch(getAllDogs());
      }, []);

      const sortByName = () => {
        const sortedBreeds = [...breeds].sort((a, b) => {
          const nameA = a.name.toUpperCase();
          const nameB = b.name.toUpperCase();
    
          if (nameA < nameB) {
            return sortByNameAsc ? -1 : 1;
          }
          if (nameA > nameB) {
            return sortByNameAsc ? 1 : -1;
          }
    
          return 0;
        });
    
        setSortByNameAsc(!sortByNameAsc);
        dispatch({ type: "SET_DOGS", payload: sortedBreeds });
      };
    
      const sortByWeight = () => {
        const sortedBreeds = [...breeds].sort((a, b) => {
          return sortByWeightAsc
            ? a.weight.metric - b.weight.metric
            : b.weight.metric - a.weight.metric;
        });
    
        setSortByWeightAsc(!sortByWeightAsc);
        dispatch({ type: "SET_DOGS", payload: sortedBreeds });
      };

    if (breeds.length === 0) {
          return (
            <LoadingScreen />
        )
    } 

    return (
        <>
            <div className="bodyHome">
                <NavBar />

                <div className="sortButtons">

                    <button onClick={sortByName}>
                        Sort by Name {sortByNameAsc ? "A-Z" : "Z-A"}
                    </button>

                    <button onClick={sortByWeight}>
                        Sort by Weight {sortByWeightAsc ? "Ascending" : "Descending"}
                    </button>

                </div>

                <div className="cardsBox">
                    {breeds.map((breed) => (
                        <BreedCard
                            id = {breed.id}  
                            name = {breed.name}
                            img = {breed.image.url}
                            temperament = {breed.temperament}
                            weight = {breed.weight}
                        />
                    ))}
                </div>

            </div>
        </>
    )

}

export default Home;
