import "./NavBar.modules.css"
import { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getAllDogs, sortDogsByName, sortDogsByWeight } from "../../Redux/actions";

export default function NavBar() {
    
    const dispatch = useDispatch();

    const [searchTerm, setSearchTerm] = useState("");

    const handleSortByNameAsc = () => {
        dispatch(sortDogsByName("asc"));
    };

    const handleSortByNameDesc = () => {
        dispatch(sortDogsByName("desc"));
    };

    const handleSortByWeightAsc = () => {
        dispatch(sortDogsByWeight("asc"));
    };

    const handleSortByWeightDesc = () => {
        dispatch(sortDogsByWeight("desc"));
    };

    const handleSearch = (event) => {
        setSearchTerm(event.target.value);
    };

    const breeds = useSelector((state) => state.dogs);

    const filteredBreeds = breeds.filter((breed) => {
        return breed.name.toLowerCase().includes(searchTerm.toLowerCase());
    });



    return (
        <div className="NavBar">
          <button onClick={handleSortByNameAsc}>Sort by name A - Z</button>
          <button onClick={handleSortByNameDesc}>Sort by name Z - A</button>
          <button onClick={handleSortByWeightAsc}>Sort by weight 1 - 10</button>
          <button onClick={handleSortByWeightDesc}>Sort by weight 10 - 1</button>
          <div className="searchBar">
            <input
              type="text"
              placeholder="Search dogs"
              value={searchTerm}
              onChange={handleSearch}
            />
          </div>
          <ul>
            {filteredBreeds.map((breed) => (
              <li key={breed.id}>{breed.name}</li>
            ))}
          </ul>
        </div>
      );
}
