import "./NavBar.modules.css"
import { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { sortDogsByName, sortDogsByWeight, getTemperaments} from "../../Redux/actions";

export default function NavBar() {
    
    const dispatch = useDispatch();

    const [selectedTemperaments, setSelectedTemperaments] = useState([]);

    const temperaments = useSelector((state) => state.temperaments);

    const handleSortByNameAsc = () => {
        dispatch(sortDogsByName("asc"));
    };

    const handleSortByNameDesc = () => {
        dispatch(sortDogsByName("desc"));
    };

    const handleSortByWeightAsc = () => {
        dispatch(sortDogsByWeight("asc"));
    };

    const handleSortByWeightDesc = () => {
        dispatch(sortDogsByWeight("desc"));
    };

    useEffect(() => {
        dispatch(getTemperaments());
    }, []);

    const handleSelectTemperament = (e) => {
        const selected = Array.from(e.target.selectedOptions, option => option.value);
        setSelectedTemperaments(selected);
    };

    const handleTemperamentChange = (event) => {
        const temperamentId = event.target.value; // Obtener el ID del temperamento seleccionado
        const isAlreadySelected = selectedTemperaments.includes(temperamentId);
        if (isAlreadySelected) {
            setSelectedTemperaments(selectedTemperaments.filter(id => id !== temperamentId)); // Des-seleccionar el temperamento
        } else {
            setSelectedTemperaments([...selectedTemperaments, temperamentId]); // Seleccionar el temperamento
        }
    };

    return (
        <div className="NavBar">
            <div>
                {/* Renderizar la combobox de temperamentos */}
            <select multiple value={selectedTemperaments} onChange={handleTemperamentChange}>
                {temperaments.map((temperament) => (
                    <option key={temperament.id} value={temperament.id}>
                        {temperament.temperament}
                    </option>
                ))}
            </select>
            {/* Bot√≥n para aplicar los filtros */}
            <button onClick={handleApplyFilters}>Apply filters</button>
            {/* Botones de ordenamiento de perros */}
                <button onClick={handleApplyFilters}>Apply</button>
            </div>
          <button onClick={handleSortByNameAsc}>Sort by name A - Z</button>
          <button onClick={handleSortByNameDesc}>Sort by name Z - A</button>
          <button onClick={handleSortByWeightAsc}>Sort by weight 1 - 10</button>
          <button onClick={handleSortByWeightDesc}>Sort by weight 10 - 1</button>
        </div>
      );
}
