import { GET_ALL_DOGS, GET_DOG_DETAILS, FILTER_DOGS_BY_TEMPERAMENT, GET_TEMPERAMENTS, CLEAN_DOG_DETAILS, SORT_DOGS_BY_NAME, SORT_DOGS_BY_WEIGHT } from "./action-types";

export const getAllDogs = () => {
    return function(dispatch) {
        fetch("http://localhost:3001/dogs")
        .then(result => result.json())
        .then(data => {
            console.log(data)
            return dispatch({type: GET_ALL_DOGS, payload: data});
        })
    }
}

export const getDogDetails = (name) => {
    return function(dispatch) {
        fetch(`http://localhost:3001/dogs?name=${name}`)
        .then(result => result.json())
        .then(dog => {
            console.log(dog)
            return dispatch({type: GET_DOG_DETAILS, payload: dog})
        })
    }
}

export const getTemperaments = () => {
    return function(dispatch) {
        fetch("http://localhost:3001/temperaments")
        .then(result => result.json())
        .then(data => {
            console.log(data)
            return dispatch({type: GET_TEMPERAMENTS, payload: data})
        })
    }
}

export const filterDogsByTemperament = (temperaments) => {
    return {
      type: FILTER_DOGS_BY_TEMPERAMENT,
      payload: temperaments,
    };
};

export const getFilteredDogs = (dogs, selectedTemperaments) => {
    if (selectedTemperaments.length === 0) {
      return dogs;
    }
    return dogs.filter((dog) =>
      selectedTemperaments.every((temperament) =>
        dog.temperament?.includes(temperament)
      )
    );
  };

export const cleanDogDetails = () => {
    return {type: CLEAN_DOG_DETAILS}
}

export const sortDogsByName = (order) => ({
    type: SORT_DOGS_BY_NAME,
    payload: order
  });
  
  export const sortDogsByWeight = (order) => ({
    type: SORT_DOGS_BY_WEIGHT,
    payload: order
  });
