import { useSelector, useDispatch } from "react-redux";
import { useEffect, useState } from "react";
import BreedCard from "../BreedCard/BreedCard";
import LoadingScreen from "../LoadingScreen/LoadingScreen"
import { getAllDogs, sortDogsByName, sortDogsByWeight } from "../../Redux/actions";
import NavBar from "../NavBar/NavBar";
import "./Home.modules.css"
/*--------------------------------------------------------------------*/

const Home = () => {

    const dispatch = useDispatch();
    
    const [searchTerm, setSearchTerm] = useState("");
    const [sortedBreeds, setSortedBreeds] = useState(filteredBreeds);
    
    const breeds = useSelector((state) => state.dogs);

    useEffect(() => {
        dispatch(getAllDogs());
      }, []);

    if (breeds.length === 0) {
          return (
            <LoadingScreen />
        )
    } 

    const filteredBreeds = breeds.filter((breed) => 
        breed.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    

    const handleSortByNameAsc = () => {
        const sortedBreeds = [...filteredBreeds].sort((a, b) => {
            const nameA = a.name.toLowerCase();
            const nameB = b.name.toLowerCase();
            return nameA.localeCompare(nameB);
        });
        setSortedBreeds(sortedBreeds);
    };

    const handleSortByNameDesc = () => {
        const sorted = [...filteredBreeds].sort((a, b) => {
            const nameA = a.name.toLowerCase();
            const nameB = b.name.toLowerCase();
            return nameB.localeCompare(nameA);
        });
        setSortedBreeds(sorted);
    };

    const handleSortByWeightAsc = () => {
        const sorted = [...filteredBreeds].sort((a, b) => {
            return a.weight.metric - b.weight.metric;
        });
        setSortedBreeds(sorted);
    };

    const handleSortByWeightDesc = () => {
        const sorted = [...filteredBreeds].sort((a, b) => {
            return b.weight.metric - a.weight.metric;
        });
        setSortedBreeds(sorted);
    };
    return (
        <>
            <div className="bodyHome">
                <NavBar />

                <div className="searchBar">
                    <input 
                        type="text"
                        placeholder="Search by name"
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                    />
                </div>

                <div className="buttons">
                    <button onClick={handleSortByNameAsc}>Sort by name A - Z</button>
                    <button onClick={handleSortByNameDesc}>Sort by name Z - A</button>
                    <button onClick={handleSortByWeightAsc}>Sort by weight 1 - 10</button>
                    <button onClick={handleSortByWeightDesc}>Sort by weight 10 - 1</button>
                </div>

                <div className="cardsBox">
                    {filteredBreeds.map((breed) => (
                        <BreedCard
                            id = {breed.id}  
                            name = {breed.name}
                            img = {breed.image.url}
                            temperament = {breed.temperament}
                            weight = {breed.weight}
                        />
                    ))}
                </div>

            </div>
        </>
    )

}

export default Home;
