import { getTemperaments } from "../../Redux/actions";
import { useSelector, useDispatch } from "react-redux";
import { useEffect, useState, useCallback } from "react";
import "./Form.modules.css"
export default function Form() {

    const dispatch = useDispatch();

    const [name, setName] = useState("");
    const [minWeight, setMinWeight] = useState("")
    const [maxWeight, setMaxWeight] = useState("")
    const [minHeight, setMinHeight] = useState("")
    const [maxHeight, setMaxHeight] = useState("")
    const [minLifeSpan, setMinLifeSpan] = useState("")
    const [maxLifeSpan, setMaxLifeSpan] = useState("")
    const [selectedTemperaments, setSelectedTemperaments] = useState([])
    const [imageURL, setImageURL] = useState("");

    const temperaments = useSelector((state) => state.temperaments)

    useEffect(() => {
        dispatch(getTemperaments());
    }, []);

    const nameHandler = (event) => {
        setName(event.target.value)
    }
    
    const minWeightHandler = (event) => {
        setMinWeight(event.target.value)
    }
    
    const maxWeightHandler = (event) => {
        setMaxWeight(event.target.value)
    }
    
    const minHeightHandler = (event) => {
        setMinHeight(event.target.value)
    }
    
    const maxHeightHandler = (event) => {
        setMaxHeight(event.target.value)
    }
    
    const minLifeSpanHandler = (event) => {
        setMinLifeSpan(event.target.value)
    }
    
    const maxLifeSpanHandler = (event) => {
        setMaxLifeSpan(event.target.value)
    }

    const handleInputChange = (event) => {
        setImageURL(event.target.value);
      };
    

      console.log(selectedTemperaments)

    const handleSelectTemperament = useCallback((e) => {
        const selected = Array.from(e.target.selectedOptions, (option) => option.value);
        setSelectedTemperaments((prevSelectedTemperaments) => {
          if (selected.length === 0) {
            return [];
          } else if (prevSelectedTemperaments.includes(selected[0])) {
            return prevSelectedTemperaments.filter((temperament) => temperament !== selected[0]);
          } else {
            const newSelectedTemperaments = [...prevSelectedTemperaments];
            selected.forEach((temperament) => {
              if (!newSelectedTemperaments.includes(temperament)) {
                newSelectedTemperaments.push(temperament);
              }
            });
            return newSelectedTemperaments;
          }
        });
      }, []);

    

    return (
        <div className="backgroundForm">
          <form>
            <h1 className="titleForm">Create your own breed!</h1>
            <hr />
      
            <label htmlFor="breedName" className="formLabel">Your breed's name: </label>
            <input type="text" name="breedName" autoComplete="off" className="formInput" value={name} onChange={nameHandler}/>

            <div className="weightSection">
                    <label htmlFor="minWeight" className="formLabel">Your breed's weight: </label>
                    <div className="textBarInput">
                    <input type="text" name="minWeight" autoComplete="off" className="formInput"value={minWeight} onChange={minWeightHandler} placeholder="Min weight of your breed"/>
                    <input type="text" name="maxWeight" autoComplete="off" className="formInput" value={maxWeight} onChange={maxWeightHandler} placeholder="Max weight of your breed"/>
                    </div>
            </div>

            <div className="heightSection">
                <label htmlFor="minHeight" className="formLabel">Your breed's min height: </label>
                <input type="text" name="minHeight" autoComplete="off" className="formInput" value={minHeight} onChange={minHeightHandler} placeholder="Min height of your breed"/>
                <input type="text" name="maxHeight" autoComplete="off" className="formInput" value={maxHeight} onChange={maxHeightHandler} placeholder="Max height of your breed"/>
            </div>
      
            <div className="lifeSpanSection">
                <label htmlFor="minLifeSpan" className="formLabel">Your breed's min life span: </label>
                <input type="text" name="minLifeSpan" autoComplete="off" className="formInput" value={minLifeSpan} onChange={minLifeSpanHandler} placeholder="Min lifespan of your breed"/>
                <input type="text" name="maxLifeSpan" autoComplete="off" className="formInput" value={maxLifeSpan} onChange={maxLifeSpanHandler} placeholder="Max lifespan of your breed"/>
            </div>

            <hr />
      
            <div className="temperamentSection">
                <label htmlFor="temperaments-selector" className="formLabel">Your breed temperaments: </label>
      
                <select className="temperaments-selector" name="temperaments-selector" multiple value={selectedTemperaments} onChange={handleSelectTemperament}>
                    {temperaments.map((temperament) => (
                        <option key={temperament.id} value={temperament.temperament}>
                        {temperament.temperament}
                        </option>
                    ))}
                </select>
      
                <label>Selected Temperaments:</label>
                <ul id="selected-temperaments">
                    {selectedTemperaments.map((temperament) => (
                        <li key={temperament}>{temperament}</li>
                    ))}
                </ul>
            </div>

            <hr />
      
            <div className="imageSection">
                <label className="formLabel">Your breed url's image: </label>
                <input type="text" value={imageURL} onChange={handleInputChange} />
                <div>
                    {imageURL && (
                    <img src={imageURL} alt="breedImage" className="formInput" />
                    )}
                </div>
            </div>

          </form>
        </div>
      );
}
