import { useSelector, useDispatch } from "react-redux";
import { useEffect, useState } from "react";
import BreedCard from "../BreedCard/BreedCard";
import LoadingScreen from "../LoadingScreen/LoadingScreen"
import { getAllDogs } from "../../Redux/actions";


const Home = () => {

    const dispatch = useDispatch();
    
    const breeds = useSelector((state) => state.dogs);

    const [loading, setLoading] = useState(true); // Agrega un estado local para controlar la carga

    useEffect(() => {
        dispatch(getAllDogs()).then(() => {
            setLoading(false); // Actualiza el estado local después de la solicitud
        });
    }, [])

    if (loading) {
          return (
              <LoadingScreen />
        )
    } 

    return (
        <>
            <div className="cardsBox">
                {breeds.map((breed) => (
                    <BreedCard
                        key={breed.id} 
                        name={breed.name}
                        img={breed.image.url}
                        temperament={breed.temperament}
                        weight={breed.weight}
                    />
                ))}
            </div>
        </>
    )

}

export default Home;
