function validateName(name, setStatus, setStatusColor) {
    if (name === "" || !/^[a-zA-Z]+$/.test(name)) {
        setStatusColor("red")
        setStatus("Please enter a valid name using only letters.");
        return false;
    } else {
        return true
    }
}

function validateWeight(minWeight, maxWeight, setStatus, setStatusColor) {
    if (
        minWeight === "" ||
        maxWeight === "" ||
        isNaN(minWeight) ||
        isNaN(maxWeight) ||
        parseFloat(minWeight) < 0 ||
        parseFloat(maxWeight) < 0 ||
        parseFloat(minWeight) >= parseFloat(maxWeight) ||
        parseFloat(maxWeight) > 90
    ) {
        setStatusColor("red")
        setStatus("Please enter valid weight values.");
        return false;
    } else {
        return true
    }
}

function validateHeight(minHeight, maxHeight, setStatus, setStatusColor) {
    if (
        minHeight === "" ||
        maxHeight === "" ||
        isNaN(minHeight) ||
        isNaN(maxHeight) ||
        parseFloat(minHeight) < 0 ||
        parseFloat(maxHeight) < 0 ||
        parseFloat(minHeight) >= parseFloat(maxHeight)
    ) {
        setStatusColor("red")
        setStatus("Please enter valid height values.");
        return false;
    } else {
        return true;
    }
}

function validateLifeSpan(minLifeSpan, maxLifeSpan, setStatus, setStatusColor) {
    if (
        minLifeSpan === "" ||
        maxLifeSpan === "" ||
        isNaN(minLifeSpan) ||
        isNaN(maxLifeSpan) ||
        parseInt(minLifeSpan) < 0 ||
        parseInt(maxLifeSpan) < 0 ||
        parseInt(minLifeSpan) >= parseInt(maxLifeSpan)
    ) {
        setStatusColor("red")
        setStatus("Please enter valid lifespan values.");
        return false;
    } else {
        return true;
    }
}

function validateTemperaments(selectedTemperaments, setStatus, setStatusColor) {
    if (selectedTemperaments.length === 0) {
        setStatusColor("red")
        setStatus("Please select at least one temperament.");
        return false;
    } else {
        return true;
    }
}

function validateURL(url) {
    return /\.(gif|jpe?g|tiff?|png|webp|bmp)$/i.test(url);
}

function validateImageURL(validateURL, imageURL) {
    if (imageURL === "" || !isValidImageURL(imageURL)) {
        setStatusColor("red")
        setStatus("Please enter a valid image URL.");
        return false;
    } else {
        return true;
    }
}

module.exports = {
    validateName,
    validateWeight,
    validateHeight,
    validateLifeSpan,
    validateTemperaments,
    validateURL,
    validateImageURL,
}
