import { useSelector, useDispatch } from "react-redux";
import { useEffect, useState } from "react";
import BreedCard from "../BreedCard/BreedCard";
import LoadingScreen from "../LoadingScreen/LoadingScreen"
import { getAllDogs } from "../../Redux/actions";
import NavBar from "../NavBar/NavBar";
import "./Home.modules.css"

function getAverageWeightInMetric(weight) {
    const weightArray = weight.metric.split(" - ");
    const weightInKgArray = weightArray.map((w) => parseFloat(w) * 0.453592);
    const averageWeightInKg = (weightInKgArray[0] + weightInKgArray[1]) / 2;
    return Math.round(averageWeightInKg * 10) / 10; // redondeamos a un decimal
}
  
/*--------------------------------------------------------------------*/

const Home = () => {

    const dispatch = useDispatch(); 

    const [searchTerm, setSearchTerm] = useState("");
    const [filteredBreeds, setFilteredBreeds] = useState([]);
    const [selectedTemperaments, setSelectedTemperaments] = useState([])
    const [currentPage, setCurrentPage] = useState(1);

    const breeds = useSelector((state) => state.dogs);
    const temperaments = useSelector((state) => state.selectedTemperaments)
    const source = useSelector((state) => state.selectedIdType)

    const itemsPerPage = 10;

    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem = indexOfLastItem - itemsPerPage;
    const currentItems = filteredBreeds.slice(indexOfFirstItem, indexOfLastItem);

    const totalPages = Math.ceil(filteredBreeds.length / itemsPerPage);

    const nextPage = () => {
        setCurrentPage(currentPage + 1);
    };

    const previousPage = () => {
        setCurrentPage(currentPage - 1);
    };

    useEffect(() => {
        dispatch(getAllDogs());
    }, []);

    useEffect(() => {
        setFilteredBreeds(breeds.filter((breed) => breed.name.toLowerCase().includes(searchTerm.toLowerCase())));
    }, [searchTerm, breeds]);

    useEffect(() => {
        setSelectedTemperaments(temperaments)
    })

    useEffect(() => {
        if (selectedTemperaments.length === 0 && source === "all") {
          setFilteredBreeds(breeds.filter((breed) => breed.name.toLowerCase().includes(searchTerm.toLowerCase())));
        } else {
          const filteredBreedsByTemperament = breeds.filter((breed) => {
            const breedTemperaments = breed.temperament?.split(', ');
            return selectedTemperaments.every((temperament) => breedTemperaments?.includes(temperament));
          });
          const filteredBreedsByIdType = filteredBreedsByTemperament.filter((breed) => {
            const breedId = breed.id;
            if (source === "number") {
              return typeof breedId === "number";
            } else if (source === "string") {
              return typeof breedId === "string";
            } else {
              return true;
            }
          });
          
          setFilteredBreeds(filteredBreedsByIdType.filter((breed) => breed.name.toLowerCase().includes(searchTerm.toLowerCase())));
        }
    }, [selectedTemperaments, source, searchTerm, breeds]);
      

    const sortDogsByNameAsc = () => {
        const sortedBreeds = [...filteredBreeds].sort((a, b) => a.name.localeCompare(b.name));
        setFilteredBreeds(sortedBreeds);
    };
    
    const sortDogsByNameDesc = () => {
        const sortedBreeds = [...filteredBreeds].sort((a, b) => b.name.localeCompare(a.name));
        setFilteredBreeds(sortedBreeds);
    };
    
    const sortDogsByWeightAsc = () => {
        const sortedBreeds = [...filteredBreeds].sort((a, b) => {
            return getAverageWeightInMetric(a.weight) - getAverageWeightInMetric(b.weight);
        });
        setFilteredBreeds(sortedBreeds);
    };
    
    const sortDogsByWeightDesc = () => {
        const sortedBreeds = [...filteredBreeds].sort((a, b) => {
            return getAverageWeightInMetric(b.weight) - getAverageWeightInMetric(a.weight);
        });
        setFilteredBreeds(sortedBreeds);
    };

    if (breeds.length === 0) {
        return <LoadingScreen />;
    }

    return (
        <>
            <div className="bodyHome">

                <NavBar />

                <div className="searchBar">
                    <input 
                        type="text"
                        placeholder="Search by name"
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                    />
                </div>

                <div className="sortBar">
                    <button onClick={sortDogsByNameAsc}>Sort by name (A-Z)</button>
                    <button onClick={sortDogsByNameDesc}>Sort by name (Z-A)</button>
                    <button onClick={sortDogsByWeightAsc}>Sort by weight (low to high)</button>
                    <button onClick={sortDogsByWeightDesc}>Sort by weight (high to low)</button>
                </div>

                {filteredBreeds.length === 0 ? (
        <div className="noDogsFound">No dogs found</div>
      ) : (
        <div className="cardsBox">
          {filteredBreeds.map((breed) => (
            <BreedCard
              key={breed.id}
              id={breed.id}  
              name={breed.name}
              img={breed.image.url}
              temperament={breed.temperament}
              weight={breed.weight}
            />
          ))}
        </div>
      )}

            </div>
        </>
    );
};

export default Home;
