import { useParams } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import { getDogDetails, cleanDogDetails } from "../../Redux/actions"; 
import { getAverageLifeSpan } from "../../Utils/GetAverageFunctions"
import logo from "../../Assets/doggoApiLogoBlack.png"
import React from "react";
import LoadingScreen from "../LoadingScreen/LoadingScreen"
import "./BreedDetail.modules.css"

const BreedDetail = () => {

    const { id } = useParams();
    const dispatch = useDispatch();
    const breed = useSelector(state => state.dogDetails)

    

    React.useEffect(() => {
        dispatch(getDogDetails(id));
        return () => dispatch(cleanDogDetails());
      }, [])

    if (!breed) {
        return (
            <LoadingScreen />
        )
    }

    console.log(breed.lifeSpan)
    console.log(getAverageLifeSpan(breed.lifeSpan))

    return (
        <>
            <div className="bodyDetail">
            <div className="formBar">

<Link to={"/"}>
  <img src={logo} alt="Doggo Api Logo" className="logoForm" />
</Link>

  <h1 className="formTitle">{breed.name}</h1>
  
<Link to = {`/Dogs`}>
    <button className="backButtonForm">Back</button>
</Link>

</div>


                <div className="imageBackground">
                    <img className="dogImg"src={breed.image.url} alt={breed.name} />
                </div>


                <div className="breedData">

                    <div className="infoMinor">

                    {breed.temperament && (
                        <p className="infoBreed">Temperaments: {breed.temperament}</p>)}

                    {breed.bred_for && (
                        <p className="infoBreed">Bred for: {breed.bred_for}</p>
                    )}

                    {breed.breed_group && (
                        <p className="infoBreed">Breed group: {breed.breed_group}</p>
                    )}

                    {breed.life_span && (
                        <p className="infoBreed">Life Span: {breed.life_span || breed.lifeSpan}</p>
                    )}

                    {breed.height && (
                        <p className="infoBreed">Height: {breed.height.metric} cm or {breed.height.imperial} inches</p>
                    )}

                    {breed.weight && (
                        <p className="infoBreed">Weight: {breed.weight.metric} kg or {breed.weight.imperial} lb</p>
                    )}

                    </div>

                    {breed.description && (
                        <p className="descriptionBreed">Description: {breed.description}</p>
                    )}

                </div>
                
                    
            </div>
        </>
    )

}

export default BreedDetail;
