import { useSelector, useDispatch } from "react-redux";
import { useEffect, useState } from "react";
import BreedCard from "../BreedCard/BreedCard";
import LoadingScreen from "../LoadingScreen/LoadingScreen"
import { getAllDogs, getTemperaments } from "../../Redux/actions";
import NavBar from "../NavBar/NavBar";
import "./Home.modules.css"
/*--------------------------------------------------------------------*/

const Home = () => {

    const dispatch = useDispatch();
    
    const [searchTerm, setSearchTerm] = useState("");
    const allDogs = useSelector((state) => state.dogs);
    const selectedTemperaments = useSelector((state) => state.filterTemperaments);

    useEffect(() => {
        dispatch(getAllDogs());
        dispatch(getTemperaments());
      }, [dispatch]);

    if (breeds.length === 0) {
          return (
            <LoadingScreen />
        )
    }

    const filteredDogs = allDogs.filter((dog) => {
        if (selectedTemperaments.length === 0) {
          return true;
        } else {
          return (
            dog.temperaments &&
            dog.temperaments.some((temp) =>
              selectedTemperaments.includes(temp.name)
            )
          );
        }
      });

    const filteredBreeds = breeds.filter((breed) => 
        breed.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    const dogsToShow = filteredBreeds(filteredDogs);

    return (
        <>
            <div className="bodyHome">
                <NavBar />

                <div className="searchBar">
                    <input 
                        type="text"
                        placeholder="Search by name"
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                    />
                </div>

                <div className="cardsBox">
                    {dogsToShow.map((breed) => (
                        <BreedCard
                            id = {breed.id}  
                            name = {breed.name}
                            img = {breed.image.url}
                            temperament = {breed.temperament}
                            weight = {breed.weight}
                        />
                    ))}
                </div>

            </div>
        </>
    )

}

export default Home;
