import { GET_ALL_DOGS, GET_DOG_DETAILS, GET_TEMPERAMENTS, FILTER_DOGS_BY_TEMPERAMENT, CLEAN_DOG_DETAILS, SORT_DOGS_BY_NAME, SORT_DOGS_BY_WEIGHT } from "./action-types";

const initialState = {
    dogs: [],
    dogDetails: null,
    temperaments: [],
    filter: {
        temperaments: [], // agregamos un objeto filter con la propiedad temperaments
    },
}

const reducer = (state = initialState, action) => {
    switch(action.type) {

        case GET_ALL_DOGS:
            return {
                ...state,
                dogs: action.payload
            }

        case GET_DOG_DETAILS:
            return {
                ...state,
                dogDetails: action.payload
            }
        
        case GET_TEMPERAMENTS:
            return {
                ...state,
                temperaments: action.payload
            }

        case CLEAN_DOG_DETAILS:
            return {
                ...state,
                dogDetails: null  
            }
        
        case FILTER_DOGS_BY_TEMPERAMENT:
            return {
                ...state,
                filter: {
                    ...state.filter,
                    temperaments: action.payload, // actualizamos el filtro de temperamentos
                },
            };

        case SORT_DOGS_BY_NAME:
            const sortedByName = [...state.dogs].sort((a, b) => {
                if (a.name > b.name) return action.payload === 'asc' ? 1 : -1;
                if (a.name < b.name) return action.payload === 'asc' ? -1 : 1;
                return 0;
            });
            return {
                ...state,
                dogs: sortedByName
            }

        case SORT_DOGS_BY_WEIGHT:
            const sortedByWeight = [...state.dogs].sort((a, b) => {
                if (a.weight.metric > b.weight.metric) return action.payload === 'asc' ? 1 : -1;
                if (a.weight.metric < b.weight.metric) return action.payload === 'asc' ? -1 : 1;
                return 0;
            });
            return {
                ...state,
                dogs: sortedByWeight
            }
            
        default:
            return {
                ...state
            }
    }
}

export default reducer;
