import { useParams } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { getDogDetails, cleanDogDetails } from "../../Redux/actions" 
import React from "react";
import LoadingScreen from "../LoadingScreen/LoadingScreen"
import "./BreedDetail.modules.css"

const BreedDetail = () => {

    const { id } = useParams();
    const dispatch = useDispatch();
    const breed = useSelector(state => state.dogDetails)

    

    React.useEffect(() => {
        dispatch(getDogDetails(id));
        return () => dispatch(cleanDogDetails());
      }, [])

    if (!breed) {
        return (
            <LoadingScreen />
        )
    }

    console.log(breed)

    return (
        <>
            <div className="bodyDetail">

                <h1 className="breedName">{breed.name}</h1>

                <div className="imageBackground">
                
                    <img className="dogImg"src={breed.image.url} alt={breed.name} />

                </div>


                    <div className="breedData">
                        <h1>Temperaments: {breed.temperament}</h1>
                        <h1>Bred for: {breed.bred_for}</h1>
                        <h1>Breed group: {breed.breed_group}</h1>
                        <h1>Life Span: {breed.life_span}</h1>
                        <h1>Height: {breed.height.metric} cm</h1>
                        <h1>Weight: {breed.weight.metric} kg</h1>
                    </div>
                
                
                    
            </div>
        </>
    )

}

export default BreedDetail;
