import "./NavBar.modules.css"
import { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { updateSelectedTemperaments, getTemperaments} from "../../Redux/actions";
export default function NavBar() {
    
    const dispatch = useDispatch();
    const [selectedTemperaments, setSelectedTemperaments] = useState([]);
    const temperaments = useSelector((state) => state.temperaments);

    useEffect(() => {
        dispatch(getTemperaments());
    }, []);

    const handleSelectTemperament = (e) => {
        const selected = Array.from(e.target.selectedOptions, (option) => option.value);
      
        // Iterar sobre los temperamentos seleccionados
        for (let i = 0; i < selected.length; i++) {
          const index = selectedTemperaments.indexOf(selected[i]);
      
          // Si el temperamento ya está seleccionado, quitarlo de la lista
          if (index !== -1) {
            setSelectedTemperaments((prevState) =>
              prevState.filter((temperament) => temperament !== selected[i])
            );
          }
          // Si el temperamento no está seleccionado, agregarlo a la lista
          else {
            setSelectedTemperaments((prevState) => [...prevState, selected[i]]);
          }
        }
      };
      

      const handleApplyFilters = () => {
        // Despachar una acción que actualice los perros mostrados en el componente Home
        dispatch(updateSelectedTemperaments(selectedTemperaments));
      };

    const handleClearSelectedTemperaments = () => {
        setSelectedTemperaments([]);
      };

      

      return (
        <div className="NavBar">
          <div>
            <label htmlFor="temperaments-select">Filter by temperament:</label>
            <select id="temperaments-select" multiple value={selectedTemperaments} onChange={handleSelectTemperament}>
              {temperaments.map((temperament) => (
                <option key={temperament.id} value={temperament.temperament}>
                  {temperament.temperament}
                </option>
              ))}
            </select>
            <div className="selected-temperaments">
              {selectedTemperaments.length > 0 && (
                <div>
                  Selected temperaments: {selectedTemperaments.join(", ")}
                  <button onClick={handleClearSelectedTemperaments}>Clear</button>
                </div>
              )}
            </div>
            <button onClick={handleApplyFilters}>Apply</button>
          </div>
        </div>
      );
    }
