import { useParams } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { getDogDetails, cleanDogDetails } from "../../Redux/actions" 
import { useEffect } from "react";
import React from "react";
import styles from "./BreedDetail.modules.css";
import LoadingScreen from "../LoadingScreen/LoadingScreen"

const BreedDetail = () => {

    const { name } = useParams();
    const dispatch = useDispatch();
    const breed = useSelector(state => state.dogDetails)

    React.useEffect(() => {
        dispatch(getDogDetails(name));
        return () => dispatch(cleanDogDetails());
      }, [])

    if (!breed) {
        return (
            <LoadingScreen />
        )
    }

    const dog = breed[0];

    console.log(dog)

    return (
        <>
            <div>
                <h1>Name: {dog.name}</h1>
                <h2>Weight: {dog.weight.imperial}</h2>
                <img src = {dog.image}/>
            </div>
        </>
    )

}

export default BreedDetail;
