import "./NavBar.modules.css"
import { useState, useEffect, useCallback } from "react";
import { useSelector, useDispatch } from "react-redux";
import { updateSelectedTemperaments, getTemperaments, updateSelectedIdType } from "../../Redux/actions";

export default function NavBar() {
    
    const dispatch = useDispatch();

    const [selectedTemperaments, setSelectedTemperaments] = useState([]);
    const [selectedIdType, setSelectedIdType] = useState("");
    const [filterLabel, setFilterLabel] = useState("");
    const [selectedCount, setSelectedCount] = useState(0);

    const temperaments = useSelector((state) => state.temperaments);

    useEffect(() => {
        dispatch(getTemperaments());
    }, []);

    useEffect(() => {
      setFilterLabel("Selected Temperaments: " + selectedTemperaments.filter(Boolean).join(", "));
  }, [selectedTemperaments]);

    // Función que se ejecutará cada vez que cambie el filtro de temperamentos
    

    const handleSelectTemperament = useCallback((e) => {
      const selected = e.target.value;
      setSelectedTemperaments((prevSelectedTemperaments) => {
        if (prevSelectedTemperaments.includes(selected)) {
          return prevSelectedTemperaments.filter((temperament) => temperament !== selected);
        } else {
          if (selectedCount < 5) {
            setSelectedCount(selectedCount + 1);
            return [...prevSelectedTemperaments, selected];
          } else {
            return prevSelectedTemperaments;
          }
        }
      });
    }, [selectedCount]);
    
    
  

    const handleClearSelectedTemperaments = () => {
      setSelectedTemperaments([]);
    };

    // Función que se ejecutará cada vez que cambie el filtro de tipo de ID
    const handleSelectIdType = useCallback((e) => {
        setSelectedIdType(e.target.value);
    }, []);

    // Función que se ejecutará cada vez que cambien los filtros
    useEffect(() => {
        dispatch(updateSelectedTemperaments(selectedTemperaments));
        dispatch(updateSelectedIdType(selectedIdType));
    }, [selectedTemperaments, selectedIdType, dispatch]);

    return (
        <div className="NavBar"> 

            <label htmlFor="temperaments-select">Filter by temperament:</label>

            <select id="temperaments-select" multiple value={selectedTemperaments} onChange={handleSelectTemperament}>
              {temperaments.map((temperament) => (
                <option key={temperament.id} value={temperament.temperament}>
                  {temperament.temperament}
                </option>
              ))}
            </select>

            <div className="selected-temperaments">
  {selectedTemperaments.length > 0 && (
    <div>
      <label>Selected Temperaments: </label>
      {selectedTemperaments.filter(Boolean).join(", ")}
      <button onClick={handleClearSelectedTemperaments}>Clear</button>
    </div>
  )}
</div>

            <div>
                <label htmlFor="id-type-select">Filter by ID type:</label>
                <select id="id-type-select" value={selectedIdType} onChange={handleSelectIdType}>
                    <option value="number">API</option>
                    <option value="string">DB</option>
                    <option value="all">All</option>
                </select>
            </div>
        </div>
    );
}
