import "./NavBar.modules.css"

import { useDispatch } from "react-redux";
import { sortDogsByName, sortDogsByWeight } from "../../Redux/actions";

export default function NavBar() {
  const dispatch = useDispatch();

  const handleSortByName = (order) => {
    dispatch(sortDogsByName(order));
  };

  const handleSortByWeight = (order) => {
    dispatch(sortDogsByWeight(order));
  };

  return (
    <div className="NavBar">
      <button onClick={() => handleSortByName("asc")}>Sort by Name (A-Z)</button>
      <button onClick={() => handleSortByName("desc")}>Sort by Name (Z-A)</button>
      <button onClick={() => handleSortByWeight("asc")}>Sort by Weight (Asc)</button>
      <button onClick={() => handleSortByWeight("desc")}>Sort by Weight (Desc)</button>
    </div>
  );
}
