import { getTemperaments } from "../../Redux/actions";
import { useSelector, useDispatch } from "react-redux";
import { useEffect, useState, useCallback } from "react";

export default function Form() {

    const dispatch = useDispatch();

    const [name, setName] = useState("");
    const [minWeight, setMinWeight] = useState("")
    const [maxWeight, setMaxWeight] = useState("")
    const [minHeight, setMinHeight] = useState("")
    const [maxHeight, setMaxHeight] = useState("")
    const [minLifeSpan, setMinLifeSpan] = useState("")
    const [maxLifeSpan, setMaxLifeSpan] = useState("")
    const [selectedTemperaments, setSelectedTemperaments] = useState([])
    const [imageURL, setImageURL] = useState("");

    const temperaments = useSelector((state) => state.temperaments)

    console.log(temperaments)

    useEffect(() => {
        dispatch(getTemperaments());
    }, []);

    const nameHandler = (event) => {
        setName(event.target.value)
    }
    
    const minWeightHandler = (event) => {
        setMinWeight(event.target.value)
    }
    
    const maxWeightHandler = (event) => {
        setMaxWeight(event.target.value)
    }
    
    const minHeightHandler = (event) => {
        setMinHeight(event.target.value)
    }
    
    const maxHeightHandler = (event) => {
        setMaxHeight(event.target.value)
    }
    
    const minLifeSpanHandler = (event) => {
        setMinLifeSpan(event.target.value)
    }
    
    const maxLifeSpanHandler = (event) => {
        setMaxLifeSpan(event.target.value)
    }

    const handleInputChange = (event) => {
        setImageURL(event.target.value);
      };
    

    const temperamentsHandler = (event) => {
        if (event.target.multiple) {
          const selectedOptions = Array.from(event.target.selectedOptions).map(option => option.value);
          setSelectedTemperaments(selectedOptions);
        } else {
          setSelectedTemperaments([event.target.value]);
        }
      }

    const handleSelectTemperament = useCallback((e) => {
        const selected = Array.from(e.target.selectedOptions, (option) => option.value);
        setSelectedTemperaments((prevSelectedTemperaments) => {
          if (selected.length === 0) {
            return [];
          } else if (prevSelectedTemperaments.includes(selected[0])) {
            return prevSelectedTemperaments.filter((temperament) => temperament !== selected[0]);
          } else {
            const newSelectedTemperaments = [...prevSelectedTemperaments];
            selected.forEach((temperament) => {
              if (!newSelectedTemperaments.includes(temperament)) {
                newSelectedTemperaments.push(temperament);
              }
            });
            return newSelectedTemperaments;
          }
        });
      }, []);

    

    return (
        <div>
          <form>
            <h1>Create your own breed!</h1>
            <hr />
      
            <label htmlFor="breedName">Your breed's name: </label>
            <input type="text" name="breedName" autoComplete="off" value={name} onChange={nameHandler}/>
      
            <label htmlFor="minWeight">Your breed's min weight: </label>
            <input type="text" name="minWeight" autoComplete="off" value={minWeight} onChange={minWeightHandler} />
      
            <label htmlFor="maxWeight">Your breed's max weight: </label>
            <input type="text" name="maxWeight" autoComplete="off" value={maxWeight} onChange={maxWeightHandler} />
      
            <label htmlFor="minHeight">Your breed's min height: </label>
            <input type="text" name="minHeight" autoComplete="off" value={minHeight} onChange={minHeightHandler} />
      
            <label htmlFor="maxHeight">Your breed's max height: </label>
            <input type="text" name="maxHeight" autoComplete="off" value={maxHeight} onChange={maxHeightHandler} />
      
            <label htmlFor="minLifeSpan">Your breed's min life span: </label>
            <input type="text" name="minLifeSpan" autoComplete="off" value={minLifeSpan} onChange={minLifeSpanHandler} />
      
            <label htmlFor="maxLifeSpan">Your breed's max life span: </label>
            <input type="text" name="maxLifeSpan" autoComplete="off" value={maxLifeSpan} onChange={maxLifeSpanHandler} />
      
            <div>
      <input type="text" value={imageURL} onChange={handleInputChange} />
      <div>
        {imageURL && (
          <img src={imageURL} alt="Imagen" style={{ maxWidth: "100%" }} />
        )}
      </div>
    </div>
      
            <select className="temperaments-select" multiple value={selectedTemperaments} onChange={handleSelectTemperament}>
              {temperaments.map((temperament) => (
                <option key={temperament.id} value={temperament.temperament}>
                  {temperament.temperament}
                </option>
              ))}
            </select>
      
            <label htmlFor="selectedTemperaments">Selected Temperaments:</label>
            <ul id="selectedTemperaments">
              {selectedTemperaments.map((temperament) => (
                <li key={temperament}>{temperament}</li>
              ))}
            </ul>
      
          </form>
        </div>
      );
}
